#! /usr/bin/env bash
# ROOT

set -e

useradd vagrant --home /home/vagrant --create-home --shell /bin/bash
passwd vagrant
mkdir -p /home/vagrant/.ssh
cd /home/vagrant/.ssh
echo "ssh-rsa  vagrant insecure public key" >> authorized_keys
chown -r vagrant:vagrant /home/vagrant/.ssh
chmod 0700 /home/vagrant/.ssh
chmod 0600 /home/vagrant/.ssh/authorized_keys
echo "vagrant ALL=(ALL) NOPASSWD: ALL" >> /etc/sudoers.d/sudo
